pragma solidity ^0.8.20;

import {SpokePoolInterface as SpokePool} from "@across-protocol/contracts-v2/contracts/interfaces/SpokePoolInterface.sol";

/**
 * @title AcrossSender
 * @author KDon.eth
 * @notice Responsible for sending tokens across the bridge
 */
abstract contract AcrossSender {

    //  ─────────────────────────────────────────────────────────────────────────────
    //  Fields
    //  ─────────────────────────────────────────────────────────────────────────────

    /// @notice The Across Bridge's SpokePool contract
    SpokePool public spokePool;

    //  ─────────────────────────────────────────────────────────────────────────────
    //  Setup
    //  ─────────────────────────────────────────────────────────────────────────────

    constructor(address _spokePool) {
        spokePool = SpokePool(_spokePool);
    }

    //  ─────────────────────────────────────────────────────────────────────────────
    //  Bridge
    //  ─────────────────────────────────────────────────────────────────────────────

    // TODO: Rename
    // function sendTxAcross(
    //     address token,
    //     uint256 amountIn,
    //     uint256 amountOut,
    // )
}

